import React, { Component } from "react";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
//import "./assets.css"

var jsonData = require('../output.json');

class AssetDetails extends Component {
  
  constructor(props) {
    super(props);
    this.state = {
      assets:[]
    }
  }

  componentDidMount(){
    fetch('http://localhost:3000/data')
         .then(results => results.json()).then(data=>this.setState({ assets: data.data}));
  }

  render() {

    var data; 

    function loadJSON(jsonfile, callback) {   

        var jsonObj = new XMLHttpRequest();
        jsonObj.overrideMimeType("application/json");
        jsonObj.open('GET', "../../file.json", true);
        jsonObj.onreadystatechange = function () {
              if (jsonObj.readyState == 4 && jsonObj.status == "200") {
                callback(jsonObj.responseText);
              }
        };
        jsonObj.send(null);  
     }

    function load() {

        loadJSON(jsonData, function(response) {
            data = JSON.parse(response);
            console.log(data);
        });
    }

    load();


    const styleObj = {
      fontSize: 13,
      color: "#4a54f1",
      textAlign: "center",           
      fontFamily: 'quicksand,sans-serif'
    }   
    const tableHeader = (
      <TableHead>
        <TableRow >
          <TableCell><b style={styleObj}>Device Name</b></TableCell>
          <TableCell><b style={styleObj}>IP Address</b></TableCell>
          <TableCell><b style={styleObj}>Mac Address</b></TableCell>
          <TableCell><b style={styleObj}>Software</b></TableCell>
          <TableCell><b style={styleObj}>Connectivity</b></TableCell>
          <TableCell><b style={styleObj}>Company Name</b></TableCell>
          <TableCell><b style={styleObj}>Time Last Shown</b></TableCell>
        </TableRow>
      </TableHead>
    );
    const content = (
      <TableBody>
        {this.state.assets.map(asset => (
          <TableRow key={asset.assetId}>
            <TableCell component="th" scope="row">
              {asset.assetName}
            </TableCell>
            <TableCell>Tests</TableCell>
            <TableCell>1</TableCell>
            <TableCell>2</TableCell>
            <TableCell>3</TableCell>
            <TableCell>4</TableCell>
            <TableCell>4</TableCell>
            <TableCell>4</TableCell>
          </TableRow>
        ))}
      </TableBody>
    );
    return (
      <div className="App-GridDiv">
        <Grid container justify="center">
          <Paper>
            <Table>
              {tableHeader}
              {content}
            </Table>
          </Paper>
        </Grid>
      </div>
    );
  }
}
//<TableCell>{asset.timeLastShown}</TableCell>
export default AssetDetails;
